import React, { useEffect, useState } from "react";
import Sawo from "sawo";
import styles from "./styles"


function App() {

  // state values
  const [payload, setUserPayload] = useState({})
  const [isLoggedIn, setIsLoggedIn] = useState(false)

  useEffect(() => {

    // Sawo Configuration, required to render form in the container
    // onSuccess callback will get invoke, after successful login

    const sawoConfig = {
      // should be same as the id of the container
      containerID: 'sawo-container',
      // can be one of 'email' or 'phone_number_sms'
      identifierType: "email",
      // Add the API key
      apiKey: "4248b081-d3c1-44a5-8fd0-af28bd1ec1ca",
      // Add a callback here to handle the payload sent by sdk
      onSuccess: onSuccessLogin
    };

    // creating instance
    let sawo = new Sawo(sawoConfig)

    // calling method to show form
    sawo.showForm();
  }, [])



  // Sawo: 
  // OnSuccess Callback method

  const onSuccessLogin = async(payload) => {
    setUserPayload(payload);
    setIsLoggedIn(true);
  }

  return ( 
    <React.Fragment>

      <div style={styles.containerStyle}>
        <section style = {{backgroundImage:"url("+"data:image/jpeg;base64,"+")"}} >
          <h1 align='center' style={{color: 'gold', backgroundColor: 'aliceblue'}}  >Sawo Login Form </h1>
          {/* Showing Successful login message */}
          {isLoggedIn && (
            <React.Fragment>
              <div style={styles.loggedin}>
              <h2>User Successfully logged In.</h2>
              <p>Welcome!, This is just a Dashboard created after the login page of Sawo for DaeDarus Hackathon.</p>
                <div>UserId: {payload.user_id}</div>
                <p style={{color:"orange" , fontFamily:"opensans"}}> Now here are the links for some following sites:- https://github.com/Miyanboy  </p>



              </div>
            </React.Fragment>
          )}

          {/* Showing login form */}
          {
            !isLoggedIn && (
              <div style={styles.formContainer} id="sawo-container">
                {/* Sawo form will appear here */}
              </div>
          )}
        </section>
      </div>
    </React.Fragment>
  );
}

export default App;
